import { Component, OnInit } from '@angular/core';
import { MobileService } from './mobile.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  constructor(private ms:MobileService){
  }
  title:string = 'FirstApp';
  mobiles:any;

  ngOnInit(): void {
    this.ms.fetchMobiles().subscribe(
      (data)=>{
        this.mobiles=data
      }
    )
    }
  }
<div *ngIf="MobileDetails;else err" class="container mob-table">
  <div>Manage Mobiles
  <button class="btn">Add Mobiles</button>
  </div>
  <table>
    <thead>
      <tr>
        <th>Id</th>
        <th>MobileName</th>
        <th>Price</th>
        <th>RAM</th>
        <th>Storage</th>
      </tr>
    </thead>
    <tbody class="text-center">
      <tr *ngFor="let mobile of MobileDetails">
        <td>{{mobile['id']}}</td>
        <td>{{mobile['mobilename']}}</td>
        <td>{{mobile['price']}}</td>
        <td>{{mobile['ram']}}</td>
        <td>{{mobile['storage']}}</td>
        <td>
        </td>
        <td>
        </td>
      </tr>
    </tbody>
  </table>
</div>
<div #err class="text-danger">
  error feching data
</div>
