import { Component, OnInit } from '@angular/core';
import { MobileService } from './mobile.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  constructor(private ms:MobileService){

  }
  title = 'FirstApp';
  mobiles=null;

  ngOnInit(): void {
    this.ms.fetchMobiles().subscribe(
      (data)=>{
        this.mobiles=data
      }
    )
    }
  }

<div *ngIf="mobiles">
  <div>Manage Mobiles
  <button class="btn">Add Mobiles</button>
  </div>
  <table>
    <thead>
      <tr>
        <th>Mobile</th>
        <th>Price</th>
        <th>RAM</th>
        <th>Storage</th>
        <th></th>
        <th></th>
      </tr>
    </thead>
    <tbody class="text-center">
      <tr *ngFor="let mobile of mobiles">
        <td>{{mobile['name']}}</td>
        <td>{{mobile['price']}}</td>
        <td>{{mobile['ram']}}</td>
        <td>{{mobile['storage']}}</td>
        <td>
          <button class="btn">Edit</button>
        </td>
        <td>
          <button class="btn">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
